<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Billing System</title>
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/css/all.min.css">
<script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/2.5.1/jspdf.umd.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/jspdf-autotable/3.5.30/jspdf.plugin.autotable.min.js"></script>
<style>
    body {
        font-family: 'Roboto', sans-serif;
        margin: 0;
        padding: 0;
        background-color: #f8f9fa;
    }
    .container {
        max-width: 800px;
        margin: 40px auto;
        padding: 40px;
        background-color: #ffffff;
        border-radius: 15px;
        box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
        transition: transform 0.3s;
    }
    .container:hover {
        transform: translateY(-5px);
    }
    h2 {
        text-align: center;
        color: #333;
        font-weight: bold;
        margin-bottom: 30px;
        text-transform: uppercase;
    }
    .btn {
        display: inline-block;
        padding: 12px 20px;
        background-color: #007bff;
        color: white;
        border-radius: 5px;
        cursor: pointer;
        transition: background-color 0.3s, box-shadow 0.3s;
        text-align: center;
        text-decoration: none;
    }
    .btn:hover {
        background-color: #0056b3;
        box-shadow: 0 6px 15px rgba(0, 0, 0, 0.2);
    }
    .select-container {
        display: flex;
        justify-content: space-between;
        align-items: center;
        margin-bottom: 20px;
    }
    select {
        padding: 10px;
        width: 250px;
        border: 1px solid #ddd;
        border-radius: 10px;
        transition: all 0.3s;
    }
    select:focus {
        border-color: #007bff;
    }
    table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 25px;
    }
    th, td {
        padding: 12px;
        text-align: left;
        border-bottom: 1px solid #ddd;
    }
    th {
        background-color: #f1f1f1;
        font-weight: bold;
        text-transform: uppercase;
        letter-spacing: 0.05em;
    }
    .total {
        text-align: right;
        font-weight: bold;
        font-size: 18px;
    }
    .buttons {
        text-align: right;
        margin-bottom: 25px;
    }
    .icon {
        margin-right: 6px;
    }
    .discount-input {
        display: flex;
        justify-content: flex-start;
        align-items: center;
        margin-bottom: 20px;
    }
    .discount-input input {
        padding: 10px;
        border: 1px solid #ddd;
        border-radius: 10px;
        width: 150px;
        transition: all 0.3s;
    }
    .discount-input input:focus {
        border-color: #007bff;
    }
    .discount-input label {
        font-weight: bold;
        margin-right: 10px;
    }
</style>
</head>
<body>
<div class="container">
    <h2><i class="fas fa-car"></i> Invoice System</h2>
    <div class="select-container">
        <select id="itemSelect">
            <option value="">Select Car Part</option>
            <option value="engine">Engine</option>
            <option value="tires">Tires</option>
            <option value="brakes">Brakes</option>
            <option value="lights">Lights</option>
        </select>
        <button class="btn" onclick="addItem()"><i class="fas fa-plus"></i> Add Part</button>
    </div>
    <table id="billTable">
        <thead>
            <tr>
                <th>Car Part</th>
                <th>Price (LKR)</th>
                <th>Quantity</th>
                <th>Remove</th>
            </tr>
        </thead>
        <tbody id="billItems">
        </tbody>
    </table>
    
    <div class="discount-input">
        <label for="discount">Discount (LKR): </label>
        <input type="number" id="discount" min="0" placeholder="0">
    </div>

    <div class="buttons">
        <button class="btn" onclick="calculateTotal()"><i class="fas fa-calculator icon"></i> Calculate Total</button>
        <button class="btn" onclick="generatePDF()"><i class="fas fa-file-pdf icon"></i> Generate PDF</button>
    </div>

    <div class="total">Total: LKR <span id="totalAmount">0.00</span></div>
</div>

<script>
var itemPrices = {
    engine: 100000,
    tires: 50000,
    brakes: 75000,
    lights: 25000
};

function addItem() {
    var selectedItem = document.getElementById("itemSelect").value;
    
    if (selectedItem && itemPrices[selectedItem]) {
        var table = document.getElementById("billItems");
        var newRow = table.insertRow();
        newRow.insertCell(0).innerText = selectedItem;
        newRow.insertCell(1).innerText = 'LKR ' + itemPrices[selectedItem].toFixed(2);
        newRow.insertCell(2).innerHTML = '<input type="number" value="1" min="1" style="width: 60px;">';
        newRow.insertCell(3).innerHTML = '<button class="btn" onclick="removeItem(this)"><i class="fas fa-trash"></i></button>';
    } else {
        alert("Please select a car part.");
    }
}

function removeItem(button) {
    var row = button.closest('tr');
    row.remove();
    calculateTotal();
}

function calculateTotal() {
    var total = 0;
    var table = document.getElementById("billTable");
    for (var i = 1; i < table.rows.length; i++) {
        var price = parseFloat(table.rows[i].cells[1].innerText.replace('LKR ', ''));
        var quantity = parseInt(table.rows[i].cells[2].querySelector('input').value);
        total += price * quantity;
    }

    // Apply discount if available
    var discount = parseFloat(document.getElementById("discount").value) || 0;
    total -= discount;

    document.getElementById("totalAmount").innerText = total.toFixed(2);
}

const logoBase64 = 'data:image/png;base64,';

function generatePDF() {
            const { jsPDF } = window.jspdf;
            const pdf = new jsPDF();

            // Add the logo at the top of the page
            pdf.addImage(logoBase64, 'PNG', 14, 10, 25, 25); // (x, y, width, height)

            pdf.setFontSize(18);
            pdf.text("Invoice - Prime Pulse Auto (Pvt) Ltd", 14, 45); // Adjusted Y-coordinate

            const headers = ["Car Part", "Price (LKR)", "Quantity"];

            const rows = [];
            const table = document.getElementById("billTable");

            for (let i = 1; i < table.rows.length; i++) {
                const row = [];
                for (let j = 0; j < 3; j++) {
                    if (j === 2) {
                        row.push(table.rows[i].cells[j].querySelector("input").value); // Quantity
                    } else {
                        row.push(table.rows[i].cells[j].innerText);
                    }
                }
                rows.push(row);
            }

            pdf.autoTable({
                head: [headers],
                body: rows,
                startY: 55, // Adjusted for logo and title
            });

            const discount = parseFloat(document.getElementById("discount").value) || 0;
            const total = parseFloat(document.getElementById("totalAmount").innerText);

            pdf.text(`Discount: LKR ${discount.toFixed(2)}`, 14, pdf.autoTable.previous.finalY + 20);
            pdf.text(`Total : LKR ${total.toFixed(2)}`, 14, pdf.autoTable.previous.finalY + 30);

            pdf.save("Service_Bill.pdf");
        }
</script>

</body>
</html>
